import {test, testComplete} from "./moca_es5";

async function hello() {
  return new Promise(function (complete, fail) {
    setTimeout(function () {
      console.log("     hello");
      complete();
    }, 1);
  });
}

async function world() {
  return new Promise(function (complete, fail) {
    setTimeout(function () {
      console.log("     world");
      fail();
    }, 1);
  });
}

(async function main() {
  await test("hello", "should print hello after 1 s", async function () {
    await hello();
  });
  await test("world", "should print hello world after 1 s", async function () {
    await hello();
    await world();
  });
  testComplete();
} ());




